planName <- "NYCTRS_CAFR17"

get_agecuts <- function(df){
	df %>% 
		mutate(age.cell = (age_lb + age_ub)/ 2) %>% 
		select(age.cell, agelb = age_lb, ageub = age_ub) 
}


