# Prepare data for the interpolation function
lactives <- list(
	agecuts = agecuts,
	yoscuts = yoscuts,
	actives.yos = 
		df_actives_impt_CAFR17 %>%
		select(
			planname = plan,
			age.cell,
			yos.cell,
			nactives = nactives_impt,
			salary   = salary_impt
		) %>%
		mutate(age = age.cell, yos = yos.cell)
)
lactives

# Grouped data
actives_grouped <-
	lactives$actives.yos %>% 
	ungroup %>% 
	select(planname, age, yos, nactives, salary) %>%
	mutate(planname = paste0(planname, "_grouped"),
				 ea = age - yos)

# interpolated data
actives_fillin <-
	fillin.actives.spreadyos.splineage(lactives) %>% 
	ungroup %>%
	select(planname, age, yos, ea,
				 #age.cell, yos.cell,
				 nactives, salary = salary.final) %>%
	mutate(planname = paste0(planname, "_fillin"))


# Combining results:
init_actives <- actives_fillin  # bind_rows(actives_fillin, actives_grouped)
init_actives

# Examine results
